sys_variable_value.value:
(function execute(inputs, outputs) {

// Pseudocode - if (the response from the REST call indicates it was OK)

if (inputs.step_input_status_code == '200')  {

  	// Use the JSON parser to parse the REST call response body.
    var response = JSON.parse(inputs.step_input_response_body);

  // Set the value of the step_output_answer output variable
  outputs.step_output_answer = response.result.answer;

}

})(inputs, outputs);

